<script setup>
import { ref, onMounted } from 'vue';
import io from 'socket.io-client';
import QRCode from 'qrcode';

import VueCookies from 'vue-cookies';

const ip = VueCookies.get('ip');
console.log(ip)

const socket = io('http://'+ip+':4001');

const sum = ref(0);

socket.on('pig', (result) => {
    console.log(result)
    sum.value = result
});

socket.emit('skarbonka');

onMounted(async () => {
    const qrcodeElement = document.getElementById("qrcode");
    const url = "https://eskarbonka.wosp.org.pl/muhusajape?lang=pl";
    try {
        const dataUrl = await QRCode.toDataURL(url);
        qrcodeElement.innerHTML = `<img src="${dataUrl}" width="400" height="400"/>`;
    } catch (err) {
        console.error(err);
    }
});
</script>